package hello;

public class CompareOper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=3;
		
		System.out.println(a+""+b+"result:"+(a>b));
		System.out.println(a+"="+b+"result:"+(a >= b));
		System.out.println(a+""+b+"result:"+(a < b));
		System.out.println(a+"="+b+"result:"+(a ==b));
		System.out.println(a+"=="+b+"result:"+(a<=b));
		System.out.println("'a'==67 result:"+('a'==97));
	}

}
